<?phpnamespace Loader;class AutoLoading{    public function __construct($basePath = '.')    {        spl_autoload_register(function ($className) use ($basePath) {            $className = ltrim($className, '\\');            $fileName  = '';            $namespace = '';            if ($lastNsPos = strripos($className, '\\')) {                $namespace = substr($className, 0, $lastNsPos);                $className = substr($className, $lastNsPos + 1);                $fileName  = str_replace('\\', DIRECTORY_SEPARATOR, $namespace) . DIRECTORY_SEPARATOR;            }            $fileName .= str_replace('_', DIRECTORY_SEPARATOR, $className) . '.php';            require_once $basePath . DIRECTORY_SEPARATOR . $fileName;        });    }}